import React from "react";

class LineBox extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const isInteractive = this.props.interactive === true;

        return (
            <div class="line-box ${isInteractive ? 'interactive' : ''}">
                <div class="line-box-header">
                    <span class="line-name">C1</span>
                    <div class="line-description-box">
                        <span class="line-stop">Carnaval de Nantes</span>
                        <span class="line-disruptions">From 03/03/24 at 7am to 9pm</span>
                    </div>
                    <img class="down-arrow" src="./assets/down-arrow.png" alt="Down arrow"/>
                </div>
            </div>
        );
    }
}

export default LineBox;
