import React from "react";

class LineBox extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isOpen: false
        };
        this.onClick = this.onClick.bind(this);
    }

    render() {
        const isInteractive = this.props.interactive === true;
        const lineTextLines = this.props.lineText.split('\n');

        return (
            <div className="line-box ${isInteractive ? 'interactive' : ''}">
                <div className="line-box-header">
                    <span className="line-name">{this.props.lineName}</span>
                    <div className="line-description-box">
                        <span className="line-stop">{this.props.lineStop}</span>
                        <span className="line-disruptions">{this.props.lineDescription}</span>
                    </div>
                    {isInteractive && <img className="down-arrow" src="./assets/down-arrow.png" alt="Down arrow" onClick={this.onClick}/>}
                </div>
                {isInteractive && (<div className="content">
                    {lineTextLines.map((line, index) => (
                        <React.Fragment key={index}>
                            {line}
                            <br />
                        </React.Fragment>
                    ))}
                </div>)}
            </div>
        );
    }

    onClick() {
        const content = document.querySelector('.content');
        const downArrow = document.querySelector('.down-arrow');

        if (content.classList.contains('open')) {
            content.classList.remove('open');
            downArrow.classList.remove('open');
        } else {
            content.classList.add('open');
            downArrow.classList.add('open');
        }
    }
}

export default LineBox;
